import React, { useState } from 'react';
import './App.css';

function App() {
  const [musteriler, setMusteriler] = useState([]);
  const [ad, setAd] = useState('');
  const [email, setEmail] = useState('');

  const musteriEkle = (e) => {
    e.preventDefault();
    if (ad && email) {
      const yeniMusteri = {
        id: Date.now(),
        ad,
        email,
      };
      setMusteriler([...musteriler, yeniMusteri]);
      setAd('');
      setEmail('');
    }
  };

  const musteriSil = (id) => {
    setMusteriler(musteriler.filter((musteri) => musteri.id !== id));
  };

  return (
    <div className="App">
      <h1>Müşteri Yönetim Sistemi</h1>
      <form onSubmit={musteriEkle}>
        <input
          type="text"
          placeholder="Müşteri Adı"
          value={ad}
          onChange={(e) => setAd(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="E-posta"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <button type="submit">Ekle</button>
      </form>

      <h2>Müşteriler</h2>
      <ul>
        {musteriler.map((musteri) => (
          <li key={musteri.id}>
            {musteri.ad} - {musteri.email}
            <button onClick={() => musteriSil(musteri.id)} style={{ marginLeft: '10px' }}>
              Sil
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
